import {
    SELECT_LAUNCHES
  } from '../types';

export const selectLaunches = (rocketId) => {
    var launches = {
        type: SELECT_LAUNCHES,
        launches: null,
    };

    const filter = "filter=rocket/rocket_name,rocket/rocket_type,launch_date_utc,launch_site/site_name_long,details";
    const url = "https://api.spacexdata.com/v3/launches";
    const rocket = "rocket_id=" + rocket_id;
    fetch(url + "?" + filter + "&" + rocket)
        .then(response => response.text())
        .then(result => {launches.launches = result})
        .catch(error => console.log(error));

    return launches;
};
