import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { Container, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import Button from '../components/Button';
import StyledCard from '../components/StyledCard';

import { addToCounter, subtractFromCounter } from '../redux/actions/counter';
import { selectRocket } from '../redux/actions/rocket';

const useStyles = makeStyles({
  container: {
    width: '86%',
    margin: '7%',
    backgroundColor: 'E1E1E1'
  },
  title: {
    marginBottom: '50px'
  }
});

const getLaunches = (rocket_id) => {
  const filter = "filter=rocket/rocket_name,rocket/rocket_type,launch_date_utc,launch_site/site_name_long,details";
  const url = "https://api.spacexdata.com/v3/launches";
  const rocket = "rocket_id=" + rocket_id;
  fetch(url + "?" + filter + "&" + rocket)
      .then(response => response.text())
      .then(result => console.log(result))
      .catch(error => console.log(error));
}

const HomePage = ({ count, rocket, incrementCount, decrementCount, selectRocket }) => {

  const classes = useStyles();

  return (
    <React.Fragment>
      <Container className={classes.container}>
        <Grid container direction="row">
          <Grid className={classes.title} item align="left" xs={12} sm={12} md={8} lg={8}>
            <h1>SpaceX Rockets</h1>
          </Grid>
          <Grid className={classes.title} item align="left" xs={12} sm={2} md={2} lg={2}>
            <h1>Search</h1>
          </Grid>
          <Grid className={classes.title} item align="left" xs={12} sm={12} md={12} lg={12}>
            <h1>Falcon 1 Missions</h1>
          </Grid>
          <Grid item align="left" xs={12} sm={12} md={12} lg={12}>
            <StyledCard rocketName="Falcon 1" type="Merlin A" details="Details" location="Location" launchDate="Launch Date" />
          </Grid>
        </Grid>
          <Button
            onClick={() => {
              incrementCount();
            }}
            title="Increment Count"
          />
          <Button
            onClick={() => {
              decrementCount();
            }}
            title="Decrement Count"
          />
          <Button
            onClick={() => {
              selectRocket("falcon1");
              getLaunches(rocket);
            }}
            title="Select Falcon 1"
          />
          <Button
            onClick={() => {
              selectRocket("falcon9");
              getLaunches(rocket);
            }}
            title="Select Falcon 9"
          />
          Your current count is {count}
          Your current rocket is {rocket}
        <Link
          to="/test"
        >
        To Test Page
        </Link>
        </Container>
    </React.Fragment>
  )
}

const mapStateToProp = (state) => {
  const { counter, rocket } = state;
  return {
    count: counter.count,
    rocket: rocket.rocketId
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    incrementCount: () => {
      dispatch(addToCounter());
    },
    decrementCount: () => {
      dispatch(subtractFromCounter());
    },
    selectRocket: rocket_id => {
      dispatch(selectRocket(rocket_id))
    }
  }
}

export default connect(mapStateToProp, mapDispatchToProps)(HomePage);
