import {
    SELECT_LAUNCHES_BEGIN,
    SELECT_LAUNCHES_SUCCESS,
    SELECT_LAUNCHES_FAILURE
  } from '../types';

export const selectLaunchesBegin = () => {
    return {
        type: SELECT_LAUNCHES_BEGIN
    };
};

export const selectLaunchesSuccess = payload => {
    return {
        type: SELECT_LAUNCHES_SUCCESS,
        payload: payload
    };
};

export const selectLaunchesFailure = error => {
    return {
        type: SELECT_LAUNCHES_FAILURE,
        payload: error
    };
};

export const selectLaunches = async rocketId => {
    var launches = {
        type: SELECT_LAUNCHES,
        launches: null,
    };

    const filter = "filter=rocket/rocket_name,rocket/rocket_type,launch_date_utc,launch_site/site_name_long,details";
    const url = "https://api.spacexdata.com/v3/launches";
    const rocket = "rocket_id=" + rocketId;
    await fetch(url + "?" + filter + "&" + rocket)
        .then(response => response.text())
        .then(result => {
            launches.launches = result;
            console.log("Before: " + launches.launches);
        })
        .catch(error => console.log(error));

    console.log("After: " + launches.launches);

    return launches;
};
