import {
  TEST_ADD_ACTION,
  TEST_SUBTRACT_ACTION,
} from '../types';

const counter = (state = {
  count: 0,
}, action) => {
  switch (action.type) {
    case TEST_ADD_ACTION: {
      return {
        ...state,
        count: state.count + 1,
      }
    }
    case TEST_SUBTRACT_ACTION: {
      return {
        ...state,
        count: state.count - 1,
      }
    }
    default:
      return state;
  }
}

export default counter;
